const validation = (values) => {
    const errors = {};
    if (!values.email) {
        errors.email = 'Requerido!!';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.email = 'Dirección de email inválida';
    } else if (values.email.length > 35) {
        errors.email = 'El email no puede tener más de 35 caracteres';
    }

    if (!values.password) {
        errors.password = 'Requerido!!';
    } else if (values.password.length < 6 || values.password.length > 8) {
        errors.password = 'La contraseña debe tener entre 6 y 8 caracteres';
    } else if (!/\d/.test(values.password)) {
        errors.password = 'La contraseña debe tener al menos un número';
    } 

    return errors;
    }

export default validation;
