import './App.css';
import Cards from './components/Cards.jsx';
import { useState } from 'react';
import NavBar from './components/NavBar/NavBar.jsx';



function App() {
   const [Characters, setCharacters] = useState([])

   const onSearch = (id) => {
      // no repeat characters in the array
      fetch(`https://rickandmortyapi.com/api/character/${id}`)
         .then(res => res.json())
         .then(data => {
            if (Characters.find((value) => value.id === data.id)) {
               alert('Character already added')
            } else {
               setCharacters([...Characters, data])
            }
         })
   }

   const onClose = (id) => {
      setCharacters(Characters.filter((value, i) => i !== parseInt(id)))
   }

   const randomCharacter = () => {
      let randomNum = Math.floor(Math.random() * 826) + 1
      fetch(`https://rickandmortyapi.com/api/character/${randomNum}`)
         .then(res => res.json())
         .then(data => {
            if (Characters.find((value) => value.id === data.id)) {
               alert('Character already added')
            } else {
               setCharacters([...Characters, data])
            }
         })
   }

   return (
      <div className='App'>
         <NavBar onSearch={onSearch} randomCharacter={randomCharacter}/>
         <Cards characters={Characters} onClose={onClose} />
      </div>
   );
}

export default App;
