const validation = ({form, setErrors, errors}) => {

    const newErrors = {...errors}
    const EMAIL = 'alanluna@gmail.com';
    const PASSWORD = 'alan05';

    if (!form.email) {
        newErrors.email = 'Es necesario un email'
    } else if (/^[\w-.]+@([\w-]+\.)+[\w-]{4}$/g.test(form.email)) {
        newErrors.email = 'Se requiere un email válido';
    } else if (form.email !== EMAIL) {
        newErrors.email = 'El email es incorrecto';
    }
    if (!form.password) {
        newErrors.password = 'Es necesario ingresar una contraseña';
    } else if (!/\d/.test(form.password)) {
        newErrors.password = 'La contraseña debe contener al menos un número';
    } else if (form.password.length < 6 || form.password.length > 8) {
        newErrors.password = 'La contraseña debe tener entre 6 y 8 caracteres';
    } else if (form.password !== PASSWORD) {
        newErrors.password = 'La contraseña es incorrecta';
    }

    return { email : errors.email, password : errors.password};
}


export default validation;
