import React, { useState } from 'react';
import './SearchBar.css'
import toast, { Toaster } from 'react-hot-toast';

export default function SearchBar(props) {
   const [Id, setId] = useState('');

   const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
         props.onSearch(Id);
         setId('');
         e.preventDefault();
      }
   };

   const verifyData = (e) => {
      const value = e.target.value;
      const isString = isNaN(value);

      if (e.key === 'Enter') {
            isString ?
            toast.error(`El dato agregado es invalido ❌`) :
            props.onSearch(Id)
         setId(0);
         e.preventDefault();
      }
   }


   let randomNum = Math.floor(Math.random() * 826) + 1;

   return (
      <div className='searchBar bar-container'>
         <button onClick={() => props.onSearch(randomNum)} className="search-button-2">Random</button>
         <input className='searchID' type='text' onKeyDown={verifyData} placeholder='Search by ID' onChange={(e) => setId(e.target.value)} />
         <Toaster position='top-left' />
      </div>
   );
}
