import React from 'react'
import { NavLink } from 'react-router-dom'
import './NavBar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faRightFromBracket } from '@fortawesome/free-solid-svg-icons'
const { addToast } = useToasts()

const NavBar = ({ logout }) => {

    const handleLogout = () => {
        addToast('¿Estás seguro de que deseas cerrar sesión?', {
            icon: '🔒',
            style: {
              borderRadius: '10px',
              background: '#333',
              color: '#fff',
            },
            duration: 5000,
            success: true,
            id: 'logoutConfirmation',
            // Opciones para personalizar la apariencia de la confirmación
            // Puedes ajustarlas según tus necesidades
            position: 'top-center',
            // Agregar botones de confirmación y cancelación
            // con sus respectivas funciones de callback
            // para manejar las respuestas del usuario
            render: ({ id, onClose }) => (
              <div>
                <p>¿Estás seguro de que deseas cerrar sesión?</p>
                <button onClick={() => handleConfirmLogout(id, onClose)}>Sí</button>
                <button onClick={onClose}>No</button>
              </div>
            ),
          });
    }

    const handleConfirmLogout = (id, onClose) => {
        // Cerrar sesión
        logout();

        // Cerrar el toast de confirmación
        onClose(id);
      };



    return (
        <nav className='navbar'>
            <div className='navbar-container'>
                <div className='navbar-logo'>
                    <h1 className='rick-and-morty'>Rick and morty</h1>
                </div>
            </div>
            <div className='navbar-links'>
                <NavLink className={`navlink ${({isActive}) => isActive ? 'active' : 'disabled'}`} to='/home'>Home</NavLink>
                <NavLink className={`navlink ${({isActive}) => isActive ? 'active' : 'disabled'}`} to='/about'>About</NavLink>
                <NavLink className={`navlink ${({isActive}) => isActive ? 'active' : 'disabled'}`} to='/favorites'>Favorites</NavLink>
                <button className='logout-button' onClick={handleLogout}><FontAwesomeIcon icon={faRightFromBracket} /></button>
            </div>
        </nav>





    )
}

export default NavBar
