import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
const { useState } = require("react");
const { useEffect } = require("react");
const { useSelector } = require("react-redux");

const Favorites = (props) => {

    const [fav, setFav] = useState(false)

    const handleFav = () => {
        if (fav) {
            props.removeFav(props.id)
            setFav(false)
        } else {
            props.addFav(props)
            setFav(true)
        }
    }

    const myFavorites = useSelector(state => state.myFavorites)

    useEffect(() => {
        myFavorites.forEach((fav) => {
            if (fav.id === props.id) {
                setFav(true);
            }
        });
    }, [myFavorites, props.id]);


    return (
        <div>
            <h1>Favorites</h1>
            <div className="favorites">
            {console.log(props)}
                {props.favorites && props.favorites.map((fav) => (
                    <div className={`card-pj ${fav.status === 'Alive' ? 'alive' : fav.status === 'Dead' ? 'dead' : ''}`}>
                        <img className="img" src={`${fav.image}`} alt={`${fav.name}`} />
                        <h1 className="name">{fav.name}</h1>
                        <h2 className={fav.status === 'Dead' ? 'status_dead' : fav.status === 'Alive' ? 'status_alive' : 'gender'}>{fav.status === 'unknown' ? 'Estado de vida desconocido' : fav.status}</h2>
                        <h2 className="gender">{fav.gender} {fav.species}</h2>
                        <h2 className="origin">{props.origin === 'unknown' ? 'Procedencia desconocida' : fav.origin}</h2>
                        <Link to={`/detail/${fav.id}`}><button className={fav.status === 'Dead' ? 'detail-dead' : fav.status === 'Alive' ? 'detail-alive' : 'detail'}>Detail</button></Link>
                        <button onClick={props.onClose} className="quit">X</button>
                        {
                            fav ? (
                                <button className='favButton isFav' onClick={handleFav}>‚ù§Ô∏è</button>
                            ) : (
                                <button className='favButton isntFav' onClick={handleFav}>ü§ç</button>
                            )
                        }
                    </div>
                ))}
            </div>
        </div>
    );
};


const mapStateToProps = (state) => {
    return {
        favorites: state.myFavorites,
    };
}

export default connect(mapStateToProps, null)(Favorites);
