import { useState } from "react";
import './Form.css';
import validation from "./validation";

const Form = ({ login }) => {

    const [form, setForm] = useState({
        email: '',
        password: ''
    })
    const [error, setError] = useState({})
    const [modalVisible, setModalVisible] = useState(false);


    const handleChange = (e) => {
        const property = e.target.name;
        const value = e.target.value;
        setForm({
            ...form,
            [property]: value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        login(form);
        setError(validation(form));
        if (Object.keys(error).length > 0) {
            setModalVisible(true);
            setError(error);
          } else {
            setModalVisible(false);
            login(form);
          }
    }
    const closeModal = () => {
        setModalVisible(false);
      };

    return (
        <>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Username</label>
                <input className="formInput" type="text" name="email" value={form.email} onChange={handleChange} />

                <label htmlFor="password">Password</label>
                <input className="formInput" type="password" name="password" value={form.password} onChange={handleChange} />

                <button className="buttonLogin" type="submit">Login</button>
            </form>

            <div class="modal-container">
                <div class="modal">
                {error.email && <p className="error">{error.email}</p>}
                {error.password && <p className="error">{error.password}</p>}
                    <span onClick={closeModal} class="close">&times;</span>
                </div>
            </div>
        </>
    );
}

export default Form
