import { ADD_FAV, REMOVE_FAV, FILTER_CARDS, ORDER_CARDS } from './actions'

const initialState = {
    allCharacters: [],
    myFavorites: []
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
            // return { ...state, myFavorites: [...state.myFavorites, action.payload] }
            // replace myFavorites with allCharacters
            return { ...state, allCharacters: [...state.allCharacters, action.payload] }
        case REMOVE_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites.filter(
                    (char) => char.id !== action.payload
                )]
            }
        case FILTER_CARDS:
            return {
                ...state,
                allCharacters: [...state.allCharacters.filter(
                    (char) => char.name.toLowerCase().includes(action.payload.toLowerCase())
                )]
            }
        case ORDER_CARDS:
            // copy of allCharacters
            const copy = [...state.allCharacters]
            // sort copy
            copy.sort((a, b) => { return a.name.localeCompare(b.name) })
            // return new state
            return {
                ...state,
                allCharacters: action.payload === 'asc' ? copy : copy.reverse()
            }
        default:
            return {...state}
    }
}

export default rootReducer
