import { ADD_FAV, REMOVE_FAV, FILTER_CARDS, ORDER_CARDS } from "./actions";

const initialState = {
  allCharacters: [],
  myFavorites: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      return {
        ...state,
        allCharacters: [...state.allCharacters],
        myFavorites: [...state.myFavorites, action.payload],
      };
    case REMOVE_FAV:
      return {
        ...state,
        myFavorites: [
          ...state.myFavorites.filter((char) => char.id !== action.payload),
        ],
      };
    case FILTER_CARDS:
      return {
        ...state,
        allCharacters: [
          ...state.allCharacters.filter((char) =>
            char.name 
          ),
        ],
      };
    case ORDER_CARDS:
      const copy = [...state.allCharacters];
      copy.sort((a, b) => {
        return a.name.localeCompare(b.name);
      });
      return {
        ...state,
        allCharacters: action.payload === "a" ? copy : copy.reverse(),
      };
    default:
      return { ...state };
  }
};

export default rootReducer;
