import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './Detail.css'

export default function Detail() {

    const { id } = useParams();

    const parsedId = parseInt(id);

    const [character, setCharacter] = useState({});

    const URL = 'https://be-a-rym.up.railway.app/api/characters'
  const API_KEY = '1f7733c3f7cc.673ee192101ab1b561a7'

    useEffect(() => {
        fetch(`${URL}/${parsedId}?key=${API_KEY}`)
            .then((res) => res.json())
            .then((data) => {
                setCharacter(data);
            });
    }, [parsedId]);

    return (
        <div className="detail-container">
            <div className={`detail-card ${character.status === 'Alive' ? 'alive' : 'dead'}`}>
                <img src={character.image} alt={character.name} />
                <div className="detail-card-info">
                    <h1>{character.name}</h1>
                    <h2>{character.species} - {character.status}</h2>
                    <p>Gender: {character.gender}</p>
                    <p>Origin: {character.origin?.name}</p>
                    <p>Location: {character.location?.name}</p>
                </div>
            </div>
        </div>
    );
    }
